import React, { useState, useEffect } from "react";
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import "../css/profile.css";

const API_URL = 'http://localhost:5000/profile';
const API_CAR_URL = 'http://localhost:5000/profile/add-car';

function Profile() {
  const [userData, setUserData] = useState({
    username: "",
    profilePicture: "",
    cars: [],
  });

  const [isEditing, setIsEditing] = useState(false);
  const [editFormData, setEditFormData] = useState({ ...userData });
  const [isAddingCar, setIsAddingCar] = useState(false);
  const [newCar, setNewCar] = useState({
    plateNumber: "",
    model: "",
    color: "",
    seats: ""
  });

  const navigate = useNavigate();

  // Fetch user data on load
  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    axios.get(API_URL, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then((response) => {
        const data = response.data;
        setUserData(data);
        setEditFormData(data);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
        navigate('/profile');
      });
  }, [navigate]);

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditFormData({
      ...editFormData,
      [name]: value
    });
  };

  const handleCarChange = (e) => {
    const { name, value } = e.target;
    setNewCar({
      ...newCar,
      [name]: value
    });
  };

  const handleSaveChanges = () => {
    const token = localStorage.getItem('token');

    setUserData(editFormData);
    setIsEditing(false);

    axios.put(API_URL, editFormData, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(response => console.log("Profile updated successfully"))
      .catch(error => console.error("Error saving profile", error));
  };

  const handleAddCar = () => {
    const token = localStorage.getItem('token');

    if (newCar.plateNumber && newCar.model) {
      axios.post(API_CAR_URL, {
        userId: userData.id,
        plateNumber: newCar.plateNumber,
        model: newCar.model,
        color: newCar.color,
        seats: newCar.seats
      }, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(response => {
          setUserData({
            ...userData,
            cars: [...userData.cars, { ...newCar, id: Date.now() }]
          });
          setIsAddingCar(false);
          setNewCar({ plateNumber: "", model: "", color: "", seats: "" });
        })
        .catch(error => console.error("Error adding car:", error));
    }
  };

  const handleRemoveCar = (carId) => {
    const updatedCars = userData.cars.filter(car => car.id !== carId);
    setUserData({ ...userData, cars: updatedCars });
  };

  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setEditFormData({
          ...editFormData,
          profilePicture: reader.result
        });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-container">
      {!isEditing ? (
        <div className="profile-view">
          <div className="profile-header">
            <Link to="/" className="back-button">Back</Link>
            <h1>My Profile</h1>
            <button className="edit-button" onClick={() => setIsEditing(true)}>Edit</button>
          </div>

          <div className="profile-picture-container">
            {userData.profilePicture ? (
              <img src={userData.profilePicture} alt="Profile" className="profile-picture" />
            ) : (
              <div className="profile-picture-placeholder">
                {userData.username.charAt(0)}
              </div>
            )}
            <h2>{userData.username || 'User'}</h2>
          </div>

          <div className="car-section">
            <h3>My Vehicles</h3>
            {userData.cars.length === 0 ? (
              <div className="no-cars">
                <p>No vehicles added yet</p>
                <button className="add-car-button" onClick={() => setIsAddingCar(true)}>+ Add Vehicle</button>
              </div>
            ) : (
              <div className="car-list">
                {userData.cars.map(car => (
                  <div className="car-item" key={car.id}>
                    <div className="car-info">
                      <div className="car-plate">{car.plateNumber}</div>
                      <div className="car-details">
                        <span className="car-model">{car.model}</span>
                        <span className="car-color">{car.color}</span>
                        {car.seats && <span className="car-seats">{car.seats} seats</span>}
                      </div>
                    </div>
                    <button className="remove-car" onClick={() => handleRemoveCar(car.id)}>Remove</button>
                  </div>
                ))}
              </div>
            )}
            <button className="add-car-button" onClick={() => setIsAddingCar(true)}>+ Add Another Vehicle</button>
          </div>
        </div>
      ) : (
        <div className="profile-edit">
          <button className="cancel-button" onClick={() => setIsEditing(false)}>Cancel</button>
          <button className="save-button" onClick={handleSaveChanges}>Save</button>
          <h3>Edit Profile</h3>
          <form className="profile-form">
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input type="text" id="username" name="username" value={editFormData.username} onChange={handleEditChange} />
            </div>
            <div className="form-group">
              <label htmlFor="profilePicture">Profile Picture</label>
              <input type="file" id="profilePicture" name="profilePicture" onChange={handleProfilePictureChange} />
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default Profile;
