import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import Axios
import "../css/aboutus.css";

const AboutUs = () => {
  const [reviews, setReviews] = useState([]);
  const [image, setImage] = useState(null);
  const [reviewText, setReviewText] = useState('');
  const [rating, setRating] = useState(0);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [message, setMessage] = useState('');

  // Fetch reviews from the backend
  useEffect(() => {
    axios.get('http://localhost:5000/reviews')  // Adjust URL if needed
      .then((response) => {
        setReviews(response.data);  // Set the fetched reviews to state
      })
      .catch((error) => {
        console.error('Error fetching reviews:', error);
      });
  }, []);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();

    // Form validation
    if (!reviewText || rating < 1 || rating > 5) {
      setMessage('Please provide a valid review and rating.');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('image', image);
      formData.append('review_text', reviewText);
      formData.append('rating', rating);

      // Make the POST request to submit the review
      const response = await axios.post('http://localhost:5000/reviews/create', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.status === 200) {
        setMessage('Review submitted successfully!');
        setIsFormVisible(false); // Hide the form after submission
      }
    } catch (error) {
      console.error('Error submitting review:', error);
      setMessage('Error submitting review, please try again.');
    }
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleRatingChange = (e) => {
    setRating(e.target.value);
  };

  return (
    <div className="about-us-container">
      {/* Featured Car Section */}
      <section className="featured-car">
        <div className="featured-car-content">
          <h2 className="featured-title">About Us</h2>
          <div className="car-model">
            <h3 className="car-model-name">Furtuor</h3>
            <span className="car-rating">4.7</span>
            <span className="reviews-count">(376 reviews)</span>
          </div>
          <p className="car-description">
            Efficient fuel-saving functionality without sacrificing engine output.
            Compact form factor ensures great maneuverability in urban settings.
            Clean K4 design offers period value considerations. Perfect dimensions
            ensure better fuel economy ratio.
          </p>
        </div>
        <div className="featured-car-image">
          <img src="your_featured_car_image.jpg" alt="Featured Car" />
        </div>
      </section>

     

      {/* Review Form */}
      {isFormVisible && (
        <div className="review-form-container">
          <h3>Submit Your Review</h3>
          <form onSubmit={handleReviewSubmit}>
            <textarea
              placeholder="Write your review here..."
              value={reviewText}
              onChange={(e) => setReviewText(e.target.value)}
            ></textarea>
            <div className="rating-input">
              <label>Rating (1-5):</label>
              <input
                type="number"
                value={rating}
                min="1"
                max="5"
                onChange={handleRatingChange}
              />
            </div>
            <div className="image-upload">
              <input type="file" onChange={handleImageChange} />
            </div>
            <button type="submit" disabled={!image || !reviewText}>
              Submit Review
            </button>
          </form>
          {message && <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>{message}</div>}
        </div>
      )}

      {/* Suggested Cars Section */}
      <section className="suggested-cars">
        <h2 className="section-title">"Shiny Cars, <span className="text-yellow">Happy Hearts"</span></h2>
        <div className="car-grid">
          {reviews.slice(0, 4).map((review) => (
            <div className="car-item" key={review.id}>
              <img src={review.image_url} alt="Suggested car" />
            </div>
          ))}
        </div>
      </section>

      {/* Customer Reviews Section */}
      <section className="customer-reviews">
        <h2 className="section-title">Customer Reviews</h2>
        <div className="reviews-container">
          {reviews.map((review) => (
            <div className="review-column" key={review.id}>
              <div className="review-card">
                <div className="review-header">
                  <div className="rating-stars">
                    <span className="star-icon">★</span>
                    <span className="rating-number">{review.rating}</span>
                  </div>
                  <div className="review-info">
                    <span className="customer-name">{review.review_text}</span>
                    <span className="review-date">{new Date(review.created_at).toLocaleDateString()}</span>
                  </div>
                </div>
                <p className="review-text">{review.review_text}</p>
                <div className="review-footer">
                  <div className="review-rating">
                    <span className="rating">{review.rating}</span>
                    <span className="rating-stars-small">★★★★★</span>
                    <span className="rating-count">220 reviews</span>
                  </div>
                  <button className="read-more-btn">Read More <span>→</span></button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Footer Section */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-bottom">
            <p className="copyright">Check out more specialties. © 2023 Your brand is a registered trademark.</p>
            <div className="social-icons">
              <a href="#" className="social-icon">f</a>
              <a href="#" className="social-icon">t</a>
              <a href="#" className="social-icon">in</a>
              <a href="#" className="social-icon">ig</a>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default AboutUs;
