import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import UserContext from '../context/UserContext';
import "../css/auth.css";

const HomePage = ({ message }) => {
  const { userData, logout } = useContext(UserContext);

  return (
    <div className="home-page">
      {message && <div className="message">{message}</div>}
      
      {userData && userData.id ? (
        // Logged in view
        <div className="welcome-container">
          <h2>Welcome, {userData.username || userData.full_name || 'User'}!</h2>
          <div className="user-actions">
            <Link to="/booking">
              <button className="home-btn">Book a Service</button>
            </Link>
            <Link to="/my-bookings">
              <button className="home-btn">My Bookings</button>
            </Link>
            <Link to="/profile">
              <button className="home-btn">My Profile</button>
            </Link>
            <button className="home-btn logout-btn" onClick={logout}>
              Logout
            </button>
          </div>
        </div>
      ) : (
        // Not logged in view
        <div className="home-buttons">
          <Link to="/login">
            <button className="home-btn">Login</button>
          </Link>
          <Link to="/register">
            <button className="home-btn">Register</button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default HomePage;
