import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom"; // Import useNavigate to handle redirection
import "../css/global.css";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // Hook for navigation

  useEffect(() => {
    // Check login status on initial render
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true); // Set logged-in state based on token
    }
  }, []);

  const handleLogout = () => {
    // Clear token and update login status
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    navigate('/homepage'); // Redirect to homepage after logout
  };

  const toggleMenu = () => setMenuOpen(!menuOpen);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest('.nav-container')) setMenuOpen(false);
    };
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, []);

  return (
    <header>
      <div className="nav-container">
        <div className="logo">
          <div className="logo-circle">
            <div className="logo-inner-circle"></div>
          </div>
          Celtical
        </div>
        <ul className={`nav-links ${menuOpen ? "active" : ""}`}>
          {/* Show Profile, Book Now, MyBookings only if user is logged in */}
          {isLoggedIn ? (
            <>
              <li><Link to="/profile">Profile</Link></li>
              <li><Link to="/bookings">Book Now</Link></li>
              <li><Link to="/MyBookings">MyBookings</Link></li>
              
            </>
          ) : (
            <>
              <li><Link to="/login">Login</Link></li>
              <li><Link to="/register">Register</Link></li>
            </>
          )}
          <li><a href="#">Service</a></li>
          <li><a href="#">Offers</a></li>
          <li><a href="#">Contact</a></li>
        </ul>

        <div className="menu-toggle" onClick={toggleMenu}>
          <span></span>
          <span></span>
          <span></span>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
