// src/components/Ai.jsx
import React, { useState } from 'react';
import axios from 'axios';
import '../css/contact.css'; // Importing the shared CSS

const Ai = () => {
  const [input, setInput] = useState('');
  const [chatLog, setChatLog] = useState([
    {
      type: 'ai',
      text: "Hi, I'm shiva's assistant! How can I help you today with your vehicle cleaning needs?",
      time: '11:30 AM',
    },
  ]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = {
      type: 'user',
      text: input,
      time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
    };

    setChatLog((prev) => [...prev, userMessage]);

    try {
      const res = await axios.post('http://localhost:5000/api/ai', {
        userId: localStorage.getItem('userId') || 'guest',
        message: input,
      });

      const aiReply = {
        type: 'ai',
        text: res.data.response,
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
      };

      setChatLog((prev) => [...prev, aiReply]);
    } catch (err) {
      console.error('AI error:', err);
      alert('Something went wrong with the server. Please try again later.');
    }

    setInput('');
  };

  return (
    <div className="help-section">
      <div className="ai-card">
        <h2>AI Assistant</h2>
        <p>Get instant help with our AI</p>

        <div className="ai-chat">
          {chatLog.map((msg, index) => (
            <div className={`ai-message ${msg.type}`} key={index}>
              <div className="ai-icon">
                <img src="../images/3.jpg" alt={`${msg.type} icon`} />
              </div>
              <div className="message-bubble">
                <p>{msg.text}</p>
                <span className="time">{msg.time}</span>
              </div>
            </div>
          ))}
        </div>

        <div className="input-area">
          <input
            type="text"
            placeholder="Type your question here..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          />
          <button className="send-button" onClick={sendMessage}>Send</button>
        </div>
      </div>

      <div className="faq-section">
        <h3>Frequently Asked Questions</h3>
        <div className="faq-item">
          <div className="faq-question">
            <span className="number">1</span>
            <p>How soon can I get an appointment?</p>
          </div>
        </div>
        <div className="faq-item">
          <div className="faq-question">
            <span className="number">2</span>
            <p>Do you offer mobile services?</p>
          </div>
        </div>
        <div className="faq-item">
          <div className="faq-question">
            <span className="number">3</span>
            <p>What payment methods do you accept?</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Ai;
