import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import UserContext from '../context/UserContext';
import '../css/MyBookings.css';

function MyBookings() {
  const [bookings, setBookings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [cancelSuccess, setCancelSuccess] = useState('');
  const [isCancelling, setIsCancelling] = useState(false);
  const { userData } = useContext(UserContext);

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    setIsLoading(true);
    setError('');
    
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/booking', {
        headers: { Authorization: `Bearer ${token}` },
      });
      
      setBookings(response.data);
    } catch (err) {
      console.error('Error fetching bookings:', err);
      setError('Unable to fetch your bookings. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancelBooking = async (bookingId) => {
    if (!window.confirm('Are you sure you want to cancel this booking?')) {
      return;
    }

    setIsCancelling(true);
    setCancelSuccess('');
    setError('');

    try {
      const token = localStorage.getItem('token');
      
      // FIXED: Match the route with the backend
      await axios.put(`http://localhost:5000/booking/${bookingId}/cancel`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      
      setCancelSuccess('Booking cancelled successfully');
      
      // Update the local state without re-fetching
      setBookings(bookings.map(booking => 
        booking.id === bookingId 
          ? { ...booking, status: 'cancelled' } 
          : booking
      ));
      
      // Refresh the booking list after a brief delay
      setTimeout(() => {
        fetchBookings();
      }, 2000);
    } catch (err) {
      console.error('Error cancelling booking:', err);
      setError('Unable to cancel booking. Please try again later.');
    } finally {
      setIsCancelling(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const formatTime = (timeString) => {
    // Check if timeString is in HH:MM:SS format
    if (/^\d{2}:\d{2}:\d{2}$/.test(timeString)) {
      const [hours, minutes] = timeString.split(':');
      const hour = parseInt(hours);
      let period = 'AM';
      
      let displayHour = hour;
      if (hour > 12) {
        displayHour = hour - 12;
        period = 'PM';
      } else if (hour === 12) {
        period = 'PM';
      } else if (hour === 0) {
        displayHour = 12;
      }
      
      return `${displayHour}:${minutes} ${period}`;
    }
    
    // If not in the expected format, just return as is
    return timeString;
  };

  // Helper function to determine status color class
  const getStatusColorClass = (status) => {
    switch (status) {
      case 'pending': return 'status-pending';
      case 'confirmed': return 'status-confirmed';
      case 'in_progress': return 'status-in-progress';
      case 'completed': return 'status-completed';
      case 'cancelled': return 'status-cancelled';
      default: return '';
    }
  };

  // Determine if booking can be cancelled (only pending or confirmed)
  const canCancelBooking = (booking) => {
    return ['pending', 'confirmed'].includes(booking.status);
  };

  return (
    <div className="my-bookings-container">
      <h2>My Bookings</h2>
      
      {error && <div className="error-message">{error}</div>}
      {cancelSuccess && <div className="success-message">{cancelSuccess}</div>}
      
      {isLoading ? (
        <div className="loading-indicator">Loading your bookings...</div>
      ) : bookings.length === 0 ? (
        <div className="no-bookings-message">
          <p>You don't have any bookings yet.</p>
          <a href="/booking" className="book-now-button">Book a Car Wash Now</a>
        </div>
      ) : (
        <div className="bookings-list">
          {bookings.map(booking => (
            <div key={booking.id} className="booking-card">
              <div className={`booking-status ${getStatusColorClass(booking.status)}`}>
                {booking.status.charAt(0).toUpperCase() + booking.status.slice(1).replace('_', ' ')}
              </div>
              
              <div className="booking-header">
                <h3>Booking #{booking.id}</h3>
                <span className="booking-date">
                  {formatDate(booking.booking_date || booking.date)}
                </span>
              </div>
              
              <div className="booking-details">
                <div className="detail-group">
                  <span className="detail-label">Service:</span>
                  <span className="detail-value">{booking.service}</span>
                </div>
                
                <div className="detail-group">
                  <span className="detail-label">Subservice:</span>
                  <span className="detail-value">{booking.subservice}</span>
                </div>
                
                <div className="detail-group">
                  <span className="detail-label">Time:</span>
                  <span className="detail-value">
                    {formatTime(booking.booking_time || booking.time_slot)}
                  </span>
                </div>
                
                <div className="detail-group">
                  <span className="detail-label">Vehicle:</span>
                  <span className="detail-value">
                    {booking.model} ({booking.plate_number}) - {booking.color}
                  </span>
                </div>
                
                <div className="detail-group">
                  <span className="detail-label">Price:</span>
                  <span className="detail-value">${booking.price}</span>
                </div>
              </div>
              
              {canCancelBooking(booking) && (
                <button
                  className="cancel-booking-button"
                  onClick={() => handleCancelBooking(booking.id)}
                  disabled={isCancelling}
                >
                  {isCancelling ? 'Cancelling...' : 'Cancel Booking'}
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MyBookings;
