import React, { useState, useContext } from "react";
import { Link } from 'react-router-dom';
import UserContext from '../context/UserContext';
import axios from 'axios';
import "../css/profile.css";

const API_URL = 'http://localhost:5000/api';
const API_CAR_URL = 'http://localhost:5000/api/add-car';

function Profile() {
  const { userData, setUser } = useContext(UserContext);
  const [isEditing, setIsEditing] = useState(false);
  const [editFormData, setEditFormData] = useState({ ...userData });
  const [isAddingCar, setIsAddingCar] = useState(false);
  const [newCar, setNewCar] = useState({
    plateNumber: "",
    model: "",
    color: "",
    seats: ""
  });

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditFormData({ ...editFormData, [name]: value });
  };

  const handleCarChange = (e) => {
    const { name, value } = e.target;
    setNewCar({ ...newCar, [name]: value });
  };

  const handleSaveChanges = () => {
    setUser(editFormData);
    setIsEditing(false);

    const token = localStorage.getItem('token');
    axios.put(API_URL, editFormData, {
      headers: { Authorization: `Bearer ${token}` }
    });
  };

  const handleAddCar = () => {
    const token = localStorage.getItem('token');
    if (newCar.plateNumber && newCar.model) {
      axios.post(API_CAR_URL, {
        userId: userData.id,
        plateNumber: newCar.plateNumber,
        model: newCar.model,
        color: newCar.color,
        seats: newCar.seats
      }, {
        headers: { Authorization: `Bearer ${token}` }
      }).then(() => {
        setUser({ ...userData, cars: [...userData.cars, { ...newCar, id: Date.now() }] });
        setIsAddingCar(false);
        setNewCar({ plateNumber: "", model: "", color: "", seats: "" });
      });
    }
  };

  const handleRemoveCar = (carId) => {
    const updatedCars = userData.cars.filter(car => car.id !== carId);
    setUser({ ...userData, cars: updatedCars });
  };
  

  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setEditFormData({ ...editFormData, profilePicture: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-container">
      {!isEditing ? (
        <div className="profile-view">
          <div className="profile-header">
            <Link to="/" className="back-button">Back</Link>
            <h1>My Profile</h1>
            <button className="edit-button" onClick={() => setIsEditing(true)}>Edit</button>
          </div>

          <div className="profile-picture-container">
            {userData.profilePicture ? (
              <img src={userData.profilePicture} alt="Profile" className="profile-picture" />
            ) : (
              <div className="profile-picture-placeholder">{userData.username?.charAt(0)}</div>
            )}
            <h2>{userData.full_name || userData.username || 'User'}</h2>
          </div>

          <div className="profile-details">
            <p><strong>Email:</strong> {userData.email}</p>
            <p><strong>Phone:</strong> {userData.phone}</p>
            <p><strong>Address:</strong> {userData.address}</p>
          </div>

          <div className="car-section">
            <h3>My Vehicles</h3>
            {userData.cars.length === 0 ? (
              <div className="no-cars">
                <p>No vehicles added yet</p>
                <button className="add-car-button" onClick={() => setIsAddingCar(true)}>+ Add Vehicle</button>
              </div>
            ) : (
              <div className="car-list">
                {userData.cars.map(car => (
                  <div className="car-item" key={car.id}>
                    <div className="car-info">
                      <div className="car-plate">{car.plateNumber}</div>
                      <div className="car-details">
                        <span className="car-model">{car.model}</span>
                        <span className="car-color">{car.color}</span>
                        {car.seats && <span className="car-seats">{car.seats} seats</span>}
                      </div>
                    </div>
                    <button className="remove-car" onClick={() => handleRemoveCar(car.id)}>Remove</button>
                  </div>
                ))}
              </div>
            )}
            <button className="add-car-button" onClick={() => setIsAddingCar(true)}>+ Add Another Vehicle</button>
          </div>
        </div>
      ) : (
        <div className="profile-edit">
          <button className="cancel-button" onClick={() => setIsEditing(false)}>Cancel</button>
          <button className="save-button" onClick={handleSaveChanges}>Save</button>
          <h3>Edit Profile</h3>
          <form className="profile-form">
            <div className="form-group">
              <label>Username</label>
              <input type="text" name="username" value={editFormData.username} onChange={handleEditChange} />
            </div>
            <div className="form-group">
              <label>Full Name</label>
              <input type="text" name="full_name" value={editFormData.full_name || ''} onChange={handleEditChange} />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input type="email" name="email" value={editFormData.email} onChange={handleEditChange} />
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input type="text" name="phone" value={editFormData.phone || ''} onChange={handleEditChange} />
            </div>
            <div className="form-group">
              <label>Address</label>
              <input type="text" name="address" value={editFormData.address || ''} onChange={handleEditChange} />
            </div>
            <div className="form-group">
              <label>Profile Picture</label>
              <input type="file" name="profilePicture" onChange={handleProfilePictureChange} />
            </div>
          </form>
        </div>
      )}

      {isAddingCar && (
        <div className="add-car-form">
          <h3>Add Vehicle</h3>
          <form>
            <div className="form-group">
              <label>Plate Number</label>
              <input type="text" name="plateNumber" value={newCar.plateNumber} onChange={handleCarChange} />
            </div>
            <div className="form-group">
              <label>Model</label>
              <input type="text" name="model" value={newCar.model} onChange={handleCarChange} />
            </div>
            <div className="form-group">
              <label>Color</label>
              <input type="text" name="color" value={newCar.color} onChange={handleCarChange} />
            </div>
            <div className="form-group">
              <label>Seats</label>
              <input type="text" name="seats" value={newCar.seats} onChange={handleCarChange} />
            </div>
            <button type="button" onClick={handleAddCar}>Add Car</button>
            <button type="button" onClick={() => setIsAddingCar(false)}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default Profile;
