import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import UserContext from '../context/UserContext';
import axios from 'axios';
import '../css/MyBookings.css';

export default function MyBookings() {
  const { userData, setUser, refreshUserData } = useContext(UserContext);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [cancelSuccess, setCancelSuccess] = useState('');

  // Use the bookings data from userData and refresh as needed
  useEffect(() => {
    const loadBookings = async () => {
      setLoading(true);
      try {
        // Refresh user data if available
        if (refreshUserData) {
          await refreshUserData();
        }
        
        // Check if userData has bookings
        if (userData && userData.bookings && Array.isArray(userData.bookings)) {
          setBookings(userData.bookings);
        } else {
          // Try to fetch bookings directly as fallback
          const token = localStorage.getItem('token');
          const response = await axios.get('http://localhost:5000/booking', {
            headers: { Authorization: `Bearer ${token}` }
          });
          
          if (response.data) {
            setBookings(response.data);
            
            // Update userData with the fetched bookings
            if (userData && setUser) {
              const updatedUserData = { ...userData, bookings: response.data };
              setUser(updatedUserData);
            }
          }
        }
      } catch (err) {
        console.error('Error loading bookings:', err);
        setError('Failed to load your bookings. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    loadBookings();
  }, [userData, refreshUserData, setUser]);

  // Function to cancel a booking
  const handleCancelBooking = async (bookingId) => {
    if (!window.confirm('Are you sure you want to cancel this booking?')) {
      return;
    }

    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:5000/api/bookings/${bookingId}/cancel`,
        { status: 'cancelled' },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Update the bookings list
      const updatedBookings = bookings.map(booking => 
        booking.id === bookingId ? { ...booking, status: 'cancelled' } : booking
      );
      
      // Update local state
      setBookings(updatedBookings);
      
      // If userData has bookings, update them too
      if (userData && userData.bookings) {
        const updatedUserData = {
          ...userData,
          bookings: updatedBookings
        };
        setUser(updatedUserData);
      }
      
      // Refresh user data to ensure consistency
      if (refreshUserData) {
        await refreshUserData();
      }
      
      setCancelSuccess(`Booking #${bookingId} has been cancelled successfully.`);
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setCancelSuccess('');
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to cancel booking. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Helper function to determine if a booking can be cancelled
  const canCancelBooking = (status) => {
    // Users can only cancel bookings with specific statuses
    const cancellableStatuses = ['pending', 'confirmed', 'work started', 'in_progress'];
    return cancellableStatuses.includes(status?.toLowerCase() || 'pending');
  };
  
  // Format date for display
  const formatDate = (dateStr) => {
    if (!dateStr) return 'N/A';
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    });
  };

  // Get car details from either the booking or userData
  const getCarDetails = (booking) => {
    // First try to get car details directly from booking if they exist
    if (booking.plate_number && booking.model) {
      return {
        plate_number: booking.plate_number,
        model: booking.model,
        color: booking.color,
        seats: booking.seats
      };
    }
    
    // Otherwise try to get from userData.cars using car_id
    if (userData?.cars && booking.car_id) {
      const car = userData.cars.find(car => car.id === booking.car_id);
      if (car) return car;
    }
    
    // If nothing found, return null
    return null;
  };

  if (loading && !bookings.length) {
    return <div className="my-bookings-container loading">Loading your bookings...</div>;
  }

  return (
    <div className="my-bookings-container">
      <div className="bookings-header">
        <h2>My Bookings</h2>
        <Link to="/booking" className="new-booking-button">New Booking</Link>
      </div>
      
      {/* Success and error messages */}
      {cancelSuccess && <div className="booking-success">{cancelSuccess}</div>}
      {error && <div className="booking-error">{error}</div>}
      {loading && <div className="booking-loading">Processing your request...</div>}
      
      {/* Refresh button */}
      <div className="refresh-container">
        <button 
          className="refresh-button" 
          onClick={() => refreshUserData && refreshUserData()}
          disabled={loading}
        >
          Refresh Bookings
        </button>
      </div>
      
      {bookings.length > 0 ? (
        <div className="bookings-list">
          {bookings.map((booking) => {
            // Safely handle status and ensure it's a string
            const status = booking.status ? booking.status.toLowerCase() : 'pending';
            const carDetails = getCarDetails(booking);
            
            // Determine what service/subservice fields to display based on available data
            const serviceDisplay = booking.service_type || booking.service || 'Service';
            const subserviceDisplay = booking.special_requests || booking.subservice || '';
            
            return (
              <div className="booking-card" key={booking.id}>
                <div className="booking-header">
                  <h3>Booking ID: {booking.id}</h3>
                  <span className={`status ${status}`}>{booking.status || 'Pending'}</span>
                </div>
                
                <div className="booking-details">
                  {/* Customer info section */}
                  <div className="booking-info">
                    <p><strong>Customer:</strong> {booking.customer_name || userData.full_name || userData.username}</p>
                    <p><strong>Email:</strong> {booking.email || userData.email}</p>
                    <p><strong>Phone:</strong> {booking.phone || userData.phone_number}</p>
                    <p><strong>Service:</strong> {serviceDisplay}</p>
                    {subserviceDisplay && <p><strong>Details:</strong> {subserviceDisplay}</p>}
                  </div>
                  
                  {/* Car details section */}
                  {carDetails && (
                    <div className="booking-car-details">
                      <p><strong>Car Plate:</strong> {carDetails.plate_number}</p>
                      <p><strong>Model:</strong> {carDetails.model}</p>
                      <p><strong>Color:</strong> {carDetails.color}</p>
                      {carDetails.seats && <p><strong>Seats:</strong> {carDetails.seats}</p>}
                    </div>
                  )}
                  
                  {/* Price section */}
                  {booking.price && (
                    <div className="booking-price">
                      <p><strong>Price:</strong> ${booking.price}</p>
                    </div>
                  )}
                  
                  {/* Time section */}
                  <div className="booking-time">
                    <p><strong>Date:</strong> {formatDate(booking.booking_date || booking.date)}</p>
                    <p><strong>Time:</strong> {booking.booking_time || booking.time_slot}</p>
                    {booking.created_at && <p><strong>Created:</strong> {formatDate(booking.created_at)}</p>}
                  </div>
                  
                  {/* Status explanation */}
                  <div className="booking-status-info">
                    {status === 'pending' && (
                      <p>Your booking is awaiting confirmation.</p>
                    )}
                    {status === 'confirmed' && (
                      <p>Your booking has been confirmed. Please bring your car at the scheduled time.</p>
                    )}
                    {(status === 'in_progress' || status === 'work started') && (
                      <p>We're currently servicing your car.</p>
                    )}
                    {status === 'ready to collect' && (
                      <p>Your car is ready! You can pick it up at your convenience.</p>
                    )}
                    {(status === 'completed' || status === 'delivered') && (
                      <p>Service completed. Thank you for your business!</p>
                    )}
                    {status === 'cancelled' && (
                      <p>This booking has been cancelled.</p>
                    )}
                  </div>
                  
                  {/* Cancel button */}
                  {canCancelBooking(status) && (
                    <div className="booking-actions">
                      <button 
                        className="cancel-booking-btn"
                        onClick={() => handleCancelBooking(booking.id)}
                        disabled={loading}
                      >
                        Cancel Booking
                      </button>
                    </div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <div className="no-bookings">
          <p>You don't have any bookings yet.</p>
          <Link to="/booking" className="booking-link">Make your first booking</Link>
        </div>
      )}
    </div>
  );
}
