import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom"; // Import useNavigate to handle redirection
import "../css/global.css";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate(); // Hook for navigation

  const handleLogout = () => {
    // Clear the token from localStorage (removes the authentication token)
    localStorage.removeItem('token');
    navigate('/'); // Redirect to the homepage after logout
  };

  const toggleMenu = () => setMenuOpen(!menuOpen);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest('.nav-container')) setMenuOpen(false);
    };
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, []);

  return (
    <header>
      <div className="nav-container">
        <div className="logo">
          <div className="logo-circle">
            <div className="logo-inner-circle"></div>
          </div>
          Celtical
        </div>

        <ul className={`nav-links ${menuOpen ? "active" : ""}`}>
          {/* Always show Profile, Book Now, MyBookings */}
          <li><Link to="/profile">Profile</Link></li>
          <li><Link to="/booking">Book Now</Link></li>
          <li><Link to="/mybookings">My Bookings</Link></li>

          {/* Services, Offers, and Contact always shown */}
          <li><a href="#">Service</a></li>
          <li><a href="#">Offers</a></li>
          <li><a href="#">Contact</a></li>

          {/* Show Login/Logout links */}
          {localStorage.getItem('token') ? (
            <li><button onClick={handleLogout}>Logout</button></li>
          ) : (
            <>
              <li><Link to="/login">Login</Link></li>
              <li><Link to="/register">Register</Link></li>
            </>
          )}
        </ul>

        <div className="menu-toggle" onClick={toggleMenu}>
          <span></span>
          <span></span>
          <span></span>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
