import React, { useState, useEffect } from "react";
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios'; // For API requests
import "../css/profile.css";

const API_URL = 'http://localhost:5000/profile';

function Profile() {
  const [userData, setUserData] = useState({
    fullName: "",
    profilePicture: "",
    cars: [],
  });

  const [isEditing, setIsEditing] = useState(false);
  const [editFormData, setEditFormData] = useState({ ...userData });
  const [isAddingCar, setIsAddingCar] = useState(false);
  const [newCar, setNewCar] = useState({
    plateNumber: "",
    model: "",
    color: "",
    seats: ""
  });

  const navigate = useNavigate();

  // Fetch user data on load
  useEffect(() => {
    // Retrieve session info via cookies (No need for a token from localStorage)
    axios.get(API_URL, { withCredentials: true })  // withCredentials to send the session cookie
      .then((response) => {
        const data = response.data;
        setUserData(data);
        setEditFormData(data);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
        // Redirect to login if token is invalid or session is not found
        navigate('/login');
      });
  }, [navigate]);

  // Handle input changes for profile editing
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditFormData({
      ...editFormData,
      [name]: value
    });
  };

  // Handle car form input changes
  const handleCarChange = (e) => {
    const { name, value } = e.target;
    setNewCar({
      ...newCar,
      [name]: value
    });
  };

  // Save profile changes
  const handleSaveChanges = () => {
    setUserData(editFormData);
    setIsEditing(false);
    // Send updated profile data to the backend
    axios.put(API_URL, editFormData, {
      withCredentials: true, // Ensure the session cookie is sent
    })
    .then(response => {
      console.log("Profile updated successfully:", response);
    })
    .catch(error => {
      console.error("Error saving profile:", error);
    });
  };

  // Add new car
  const handleAddCar = () => {
    if (newCar.plateNumber && newCar.model) {
      const updatedCars = [...userData.cars, { ...newCar, id: Date.now() }];
      setUserData({ ...userData, cars: updatedCars });
      setNewCar({
        plateNumber: "",
        model: "",
        color: "",
        seats: ""
      });
      setIsAddingCar(false);
    }
  };

  // Remove car
  const handleRemoveCar = (carId) => {
    const updatedCars = userData.cars.filter(car => car.id !== carId);
    setUserData({ ...userData, cars: updatedCars });
  };

  // Handle profile picture change
  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setEditFormData({
          ...editFormData,
          profilePicture: reader.result
        });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-container">
      {!isEditing ? (
        // View Mode
        <div className="profile-view">
          <div className="profile-header">
            <Link to="/" className="back-button">
              {/* Back button SVG */}
            </Link>
            <h1>My Profile</h1>
            <button className="edit-button" onClick={() => setIsEditing(true)}>
              {/* Edit button SVG */}
            </button>
          </div>

          <div className="profile-picture-container">
            {userData.profilePicture ? (
              <img src={userData.profilePicture} alt="Profile" className="profile-picture" />
            ) : (
              <div className="profile-picture-placeholder">
                {userData.fullName.charAt(0)}
              </div>
            )}
            <h2>{userData.fullName || 'User'}</h2>
          </div>

          <div className="car-section">
            <h3>My Vehicles</h3>
            {userData.cars.length === 0 ? (
              <div className="no-cars">
                <p>No vehicles added yet</p>
                <button className="add-car-button" onClick={() => setIsAddingCar(true)}>+ Add Vehicle</button>
              </div>
            ) : (
              <div className="car-list">
                {userData.cars.map(car => (
                  <div className="car-item" key={car.id}>
                    <div className="car-info">
                      <div className="car-plate">{car.plateNumber}</div>
                      <div className="car-details">
                        <span className="car-model">{car.model}</span>
                        <span className="car-color">{car.color}</span>
                        {car.seats && <span className="car-seats">{car.seats} seats</span>}
                      </div>
                    </div>
                    <button className="remove-car" onClick={() => handleRemoveCar(car.id)}>
                      {/* Remove car SVG */}
                    </button>
                  </div>
                ))}
              </div>
            )}
            <button className="add-car-button" onClick={() => setIsAddingCar(true)}>+ Add Another Vehicle</button>
          </div>
        </div>
      ) : (
        // Edit Mode
        <div className="profile-edit">
          <button className="cancel-button" onClick={() => setIsEditing(false)}>Cancel</button>
          <button className="save-button" onClick={handleSaveChanges}>Save</button>
          <h3>Edit Profile</h3>
          <form className="profile-form">
            <div className="form-group">
              <label htmlFor="fullName">Full Name</label>
              <input type="text" id="fullName" name="fullName" value={editFormData.fullName} onChange={handleEditChange} />
            </div>
            <div className="form-group">
              <label htmlFor="profilePicture">Profile Picture</label>
              <input type="file" id="profilePicture" name="profilePicture" onChange={handleProfilePictureChange} />
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default Profile;
