import React, { createContext, useState, useEffect } from 'react';

const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  // Load user data from localStorage when app loads
  useEffect(() => {
    const storedUserData = localStorage.getItem('user');
    if (storedUserData) {
      try {
        setUserData(JSON.parse(storedUserData));
      } catch (error) {
        console.error('Error parsing stored user data', error);
        localStorage.removeItem('user');
      }
    }
    setLoading(false);
  }, []);

  // Set user data and store in localStorage
  const setUser = (data) => {
    setUserData(data);
    if (data) {
      localStorage.setItem('user', JSON.stringify(data));
    }
  };

  // Logout: Remove user data and token from localStorage
  const logout = () => {
    setUserData(null);
    localStorage.removeItem('user');
    localStorage.removeItem('token');
    localStorage.removeItem('adminToken'); // Also clear admin token if it exists
  };

  return (
    <UserContext.Provider value={{ userData, setUser, logout, loading }}>
      {children}
    </UserContext.Provider>
  );
};

export default UserContext;
