import React, { useState, useContext, useEffect } from "react";
import { Link } from "react-router-dom";
import UserContext from "../context/UserContext";
import CarManager from './CarManager'; 
import axios from "axios";
import "../css/profile.css";

const API_URL = "http://localhost:5000/api/profile";

function Profile() {
  // We now rely mainly on userData from context which auto-updates via socket
  const { userData, isConnected, refreshUserData } = useContext(UserContext);
  const [isEditing, setIsEditing] = useState(false);
  const [editFormData, setEditFormData] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [statusMessage, setStatusMessage] = useState(null);
  
  // Set form data whenever userData changes (including socket updates)
  useEffect(() => {
    if (userData) {
      setEditFormData({ ...userData });
    }
  }, [userData]);

  // Show connection status to help debug
  useEffect(() => {
    if (!isConnected && userData) {
      setStatusMessage({
        type: 'info',
        text: 'You are currently offline. Changes will sync when connection is restored.'
      });
    } else {
      // Clear the message if it was showing the offline status
      if (statusMessage && statusMessage.type === 'info' && 
          statusMessage.text.includes('offline')) {
        setStatusMessage(null);
      }
    }
  }, [isConnected, userData]);

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditFormData({ ...editFormData, [name]: value });
  };

  const handleSaveChanges = async () => {
    setIsLoading(true);
    setStatusMessage(null);

    try {
      const token = localStorage.getItem("token");
      await axios.put(API_URL, editFormData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      
      setIsEditing(false);
      setStatusMessage({ type: 'success', text: 'Profile updated successfully' });
      
      // Only manually refresh if socket is disconnected
      if (!isConnected) {
        await refreshUserData();
      }
      // Otherwise, the server will push updates through the socket
    } catch (error) {
      console.error("Error updating profile", error);
      setStatusMessage({ 
        type: 'error', 
        text: error.response?.data?.message || 'Error updating profile' 
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setEditFormData({ ...editFormData, profilePicture: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  // If userData is null or not loaded, show loading message
  if (!userData) {
    return <div className="loading-container">Loading user data...</div>;
  }

  // Determine loyalty status based on points
  const getLoyaltyStatus = (points) => {
    if (points >= 100) {
      return {
        status: "Gold Member",
        discount: "10%",
      };
    } else if (points >= 50) {
      return {
        status: "Silver Member",
        discount: "5%",
      };
    }
    return null;
  };

  const loyaltyStatus = getLoyaltyStatus(userData.points || 0);

  return (
    <div className="profile-container">
      {statusMessage && (
        <div className={`status-message ${statusMessage.type}`}>
          {statusMessage.text}
        </div>
      )}
      
      {!isConnected && (
        <div className="connection-status offline">
          ⚠️ Offline Mode - Changes will sync when you're back online
        </div>
      )}
      
      {!isEditing ? (
        <div className="profile-view">
          <div className="profile-header">
            <Link to="/" className="back-button">
              Back
            </Link>
            <h1>My Profile</h1>
            <button 
              className="edit-button" 
              onClick={() => setIsEditing(true)}
              disabled={isLoading}
            >
              Edit
            </button>
          </div>

          <div className="profile-picture-container">
            {userData.profilePicture ? (
              <img
                src={userData.profilePicture}
                alt="Profile"
                className="profile-picture"
              />
            ) : (
              <div className="profile-picture-placeholder">
                {userData.username?.charAt(0)}
              </div>
            )}
            <h2>{userData.full_name || userData.username || "User"}</h2>
          </div>

          <div className="profile-details">
            <p>
              <strong>Email:</strong> {userData.email}
            </p>
            <p>
              <strong>Phone:</strong> {userData.phone || userData.phone_number}
            </p>
            <p>
              <strong>Address:</strong> {userData.address}
            </p>
            <p>
              <strong>Points:</strong> {userData.points || 0}
            </p>
            {loyaltyStatus && (
              <div className="loyalty-status">
                <p><strong>Loyalty Status:</strong> {loyaltyStatus.status}</p>
                <p>You get {loyaltyStatus.discount} discount on all services!</p>
              </div>
            )}
          </div>

          {/* CarManager no longer needs to call refreshUserData - it gets updates from socket */}
          <CarManager 
            userData={userData} 
            isConnected={isConnected} 
            refreshUserData={refreshUserData}
          />
        </div>
      ) : (
        <div className="profile-edit">
          <button 
            className="cancel-button" 
            onClick={() => setIsEditing(false)}
            disabled={isLoading}
          >
            Cancel
          </button>
          <button 
            className="save-button" 
            onClick={handleSaveChanges}
            disabled={isLoading}
          >
            {isLoading ? 'Saving...' : 'Save'}
          </button>
          <h3>Edit Profile</h3>
          <form className="profile-form">
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                name="username"
                value={editFormData.username || ""}
                onChange={handleEditChange}
                disabled={isLoading}
              />
            </div>
            <div className="form-group">
              <label>Full Name</label>
              <input
                type="text"
                name="full_name"
                value={editFormData.full_name || ""}
                onChange={handleEditChange}
                disabled={isLoading}
              />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={editFormData.email || ""}
                onChange={handleEditChange}
                disabled={isLoading}
              />
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input
                type="text"
                name="phone"
                value={editFormData.phone || editFormData.phone_number || ""}
                onChange={handleEditChange}
                disabled={isLoading}
              />
            </div>
            <div className="form-group">
              <label>Address</label>
              <input
                type="text"
                name="address"
                value={editFormData.address || ""}
                onChange={handleEditChange}
                disabled={isLoading}
              />
            </div>
            <div className="form-group">
              <label>Profile Picture</label>
              <input
                type="file"
                name="profilePicture"
                onChange={handleProfilePictureChange}
                disabled={isLoading}
              />
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default Profile;
