import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";
import UserContext from "../context/UserContext";
import CarManager from './ar'; 
import axios from "axios";
import "../css/profile.css";

const API_URL = "http://localhost:5000/api";

function Profile() {
  const { userData, setUser } = useContext(UserContext);
  const [isEditing, setIsEditing] = useState(false);
  const [editFormData, setEditFormData] = useState({ ...userData });

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditFormData({ ...editFormData, [name]: value });
  };

  const handleSaveChanges = () => {
    setUser(editFormData);
    setIsEditing(false);

    const token = localStorage.getItem("token");
    axios
      .put(API_URL, editFormData, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        // handle successful response here if needed
      });
  };

  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setEditFormData({ ...editFormData, profilePicture: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-container">
      {!isEditing ? (
        <div className="profile-view">
          <div className="profile-header">
            <Link to="/" className="back-button">
              Back
            </Link>
            <h1>My Profile</h1>
            <button className="edit-button" onClick={() => setIsEditing(true)}>
              Edit
            </button>
          </div>

          <div className="profile-picture-container">
            {userData.profilePicture ? (
              <img
                src={userData.profilePicture}
                alt="Profile"
                className="profile-picture"
              />
            ) : (
              <div className="profile-picture-placeholder">
                {userData.username?.charAt(0)}
              </div>
            )}
            <h2>{userData.full_name || userData.username || "User"}</h2>
          </div>

          <div className="profile-details">
            <p>
              <strong>Email:</strong> {userData.email}
            </p>
            <p>
              <strong>Phone:</strong> {userData.phone}
            </p>
            <p>
              <strong>Address:</strong> {userData.address}
            </p>
          </div>

          {/* Embed CarManager here */}
          <Car userData={userData} setUser={setUser} />
        </div>
      ) : (
        <div className="profile-edit">
          <button className="cancel-button" onClick={() => setIsEditing(false)}>
            Cancel
          </button>
          <button className="save-button" onClick={handleSaveChanges}>
            Save
          </button>
          <h3>Edit Profile</h3>
          <form className="profile-form">
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                name="username"
                value={editFormData.username}
                onChange={handleEditChange}
              />
            </div>
            <div className="form-group">
              <label>Full Name</label>
              <input
                type="text"
                name="full_name"
                value={editFormData.full_name || ""}
                onChange={handleEditChange}
              />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={editFormData.email}
                onChange={handleEditChange}
              />
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input
                type="text"
                name="phone"
                value={editFormData.phone || ""}
                onChange={handleEditChange}
              />
            </div>
            <div className="form-group">
              <label>Address</label>
              <input
                type="text"
                name="address"
                value={editFormData.address || ""}
                onChange={handleEditChange}
              />
            </div>
            <div className="form-group">
              <label>Profile Picture</label>
              <input
                type="file"
                name="profilePicture"
                onChange={handleProfilePictureChange}
              />
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default Profile;
