import React, { createContext, useState, useEffect } from 'react';

// Create the UserContext
const UserContext = createContext();

// UserProvider will be used to wrap the app and make user data available across the app
export const UserProvider = ({ children }) => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // On initial load, check if user data is stored in localStorage
    const storedUserData = localStorage.getItem('user');
    if (storedUserData) {
      setUserData(JSON.parse(storedUserData)); // If available, set it to state
    }
  }, []);

  // Function to update user data
  const setUser = (data) => {
    setUserData(data);
    // Store the data in localStorage for persistence
    localStorage.setItem('user', JSON.stringify(data));
  };

  // Function to clear user data (logout)
  const logout = () => {
    setUserData(null);
    localStorage.removeItem('user'); // Clear localStorage on logout
  };

  return (
    <UserContext.Provider value={{ userData, setUser, logout }}>
      {children}
    </UserContext.Provider>
  );
};

export default UserContext;
