import React, { useState, useContext, useEffect } from "react";
import "../css/Booking.css";
import UserContext from '../context/UserContext';

const services = [
  { name: "Exterior Car Wash", price: 25, subservices: ["Basic Wash", "Premium Wash"] },
  { name: "Interior Detailing", price: 40, subservices: ["Vacuuming", "Deep Cleaning"] },
  { name: "Tire Shine", price: 15, subservices: ["Standard Shine", "Ultra Shine"] },
  { name: "Exhaust Tip Cleaning", price: 20, subservices: ["Basic Cleaning", "Advanced Polishing"] },
  { name: "Wiper Fluid Top-Up", price: 10, subservices: ["Standard Fluid", "Premium Fluid"] },
];

const timeSlots = ["9:00 AM", "11:00 AM", "1:00 PM", "3:00 PM", "5:00 PM"];

export default function Booking() {
  const { userData, setUser } = useContext(UserContext); // Use userContext
  const [selectedCar, setSelectedCar] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedService, setSelectedService] = useState(null);
  const [cart, setCart] = useState([]);
  const [selectedTime, setSelectedTime] = useState(null);
  const [bookingId, setBookingId] = useState(null);
  const [confirmationMessage, setConfirmationMessage] = useState(null);

  // Fetch car details from userData
  useEffect(() => {
    if (userData && userData.cars && userData.cars.length > 0) {
      setSelectedCar(userData.cars[0].plateNumber);  
    }
  }, [userData]);

  const addToCart = (service, subservice) => {
    setCart([...cart, { service, subservice, price: service.price }]);
  };

  const removeFromCart = (index) => {
    setCart(cart.filter((_, i) => i !== index));
  };

  const totalPrice = cart.reduce((sum, item) => sum + item.price, 0);

  const convertTo24HourFormat = (time) => {
    const [hourMinute, period] = time.split(" ");
    let [hour, minute] = hourMinute.split(":");

    if (period === "PM" && hour !== "12") {
      hour = parseInt(hour) + 12;
    } else if (period === "AM" && hour === "12") {
      hour = 0;
    }

    return `${String(hour).padStart(2, "0")}:${minute}:00`;
  };

  const handleBooking = () => {
    if (!selectedCar) return alert("Please select a car.");

    const carDetails = userData.cars.find(car => car.plateNumber === selectedCar);
    const formattedTime = convertTo24HourFormat(selectedTime);

    const bookingData = {
      customerName: userData.full_name || userData.username,
      email: userData.email,
      phone: userData.phone_number,
      service: selectedService.name,
      subservice: selectedService.subservices[0],
      price: selectedService.price,
      date: selectedDate,
      timeSlot: formattedTime,
      carNumber: selectedCar,
      carModel: carDetails?.model,
      carColor: carDetails?.color,
      carSeats: carDetails?.seats,
    };

    fetch("http://localhost:5000/booking/create", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(bookingData),
    })
      .then(res => res.json())
      .then(data => {
        console.log("Backend response:", data); // Debugging response from backend
        if (data.bookingId) {
          setBookingId(data.bookingId);
          setConfirmationMessage({
            date: selectedDate,
            time: formattedTime,
            total: totalPrice,
            bookingId: data.bookingId,
            car: carDetails,
          });

          // Reset form
          setSelectedDate(null);
          setSelectedService(null);
          setSelectedTime(null);
          setCart([]);
          setSelectedCar(null);
        } else {
          alert("Error: Booking failed. Please try again.");
        }
      })
      .catch(error => {
        console.error("Error booking:", error);
        alert("There was an error with the booking process.");
      });
  };

  return (
    <div className="booking-container">
      <h2 className="heading">Book a Car Wash</h2>

      {/* Car Selection */}
      {userData && userData.cars && userData.cars.length > 0 && (
        <div className="car-selector-container">
          <h3 className="section-title">Select Car</h3>
          <select
            value={selectedCar || ""}
            onChange={(e) => setSelectedCar(e.target.value)}
            className="car-selector"
          >
            <option value="" disabled>Select your car</option>
            {userData.cars.map((car) => (
              <option 
                key={car.plateNumber}  
                value={car.plateNumber}
              >
                {car.plateNumber} - {car.model} - {car.color} - {car.seats} seats
              </option>
            ))}
          </select>
        </div>
      )}

      {/* Select Date */}
      <div className="date-picker-container">
        <h3 className="section-title">Select Date</h3>
        <input
          type="date"
          value={selectedDate || ""}
          onChange={(e) => setSelectedDate(e.target.value)}
          min={new Date().toISOString().split("T")[0]}
          className="date-picker"
        />
      </div>

      {/* Services */}
      {selectedDate && (
        <div className="service-container">
          <h3 className="section-title">Select Service</h3>
          <div className="service-grid">
            {services.map((service) => (
              <div
                key={service.name}
                onClick={() => setSelectedService(service)}
                className="service-item"
              >
                <h4 className="service-name">{service.name}</h4>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Subservices */}
      {selectedService && (
        <div className="subservice-container">
          <h3 className="section-title">Select Subservice</h3>
          <div className="subservice-grid">
            {selectedService.subservices.map((sub) => (
              <button
                key={sub}
                onClick={() => addToCart(selectedService, sub)}
                className="subservice-button"
              >
                {sub} (+${selectedService.price})
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Cart */}
      {cart.length > 0 && (
        <div className="cart-container">
          <h3 className="section-title">Cart</h3>
          <div className="cart-content">
            {cart.map((item, index) => (
              <div key={index} className="cart-item">
                <span>{item.service.name} - {item.subservice}</span>
                <span>${item.price}</span>
                <button onClick={() => removeFromCart(index)}>Remove</button>
              </div>
            ))}
            <div className="total-price">Total: ${totalPrice}</div>
          </div>
        </div>
      )}

      {/* Time Slot */}
      {cart.length > 0 && (
        <div className="time-container">
          <h3 className="section-title">Select Time</h3>
          <div className="time-slot-grid">
            {timeSlots.map((time) => (
              <button
                key={time}
                onClick={() => setSelectedTime(time)}
                className="time-slot-button"
              >
                {time}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Confirm Booking */}
      {selectedTime && (
        <div className="confirmation-container">
          <h3 className="confirmation-title">Confirm Booking</h3>
          
          {/* Display user details and booking details */}
          <p><strong>Full Name:</strong> {userData.full_name || userData.username}</p>
          <p><strong>Email:</strong> {userData.email}</p>
          <p><strong>Phone:</strong> {userData.phone_number}</p>

          {/* Car details */}
          <p><strong>Car Plate Number:</strong> {selectedCar}</p>
          <p><strong>Car Model:</strong> {userData.cars.find(car => car.plateNumber === selectedCar)?.model}</p>
          <p><strong>Car Color:</strong> {userData.cars.find(car => car.plateNumber === selectedCar)?.color}</p>
          <p><strong>Seats:</strong> {userData.cars.find(car => car.plateNumber === selectedCar)?.seats}</p>

          {/* Service details */}
          <p><strong>Service:</strong> {selectedService?.name}</p>
          <p><strong>Subservice:</strong> {selectedService?.subservices[0]}</p>
          <p><strong>Price:</strong> ${totalPrice}</p>

          {/* Date and Time slot */}
          <p><strong>Date:</strong> {selectedDate}</p>
          <p><strong>Time Slot:</strong> {selectedTime}</p>
          
          <button className="confirm-button" onClick={handleBooking}>
            Confirm Booking
          </button>
        </div>
      )}

      {/* Display Booking Confirmation Message */}
      {confirmationMessage && (
        <div className="confirmation-message">
          <h3>Booking Confirmed!</h3>
          <p><strong>Booking ID:</strong> {confirmationMessage.bookingId}</p>
          <p><strong>Car:</strong> {confirmationMessage.car.model} - {confirmationMessage.car.color}</p>
          <p><strong>Date:</strong> {confirmationMessage.date}</p>
          <p><strong>Time:</strong> {confirmationMessage.time}</p>
          <p><strong>Total:</strong> ${confirmationMessage.total}</p>
        </div>
      )}
    </div>
  );
}
