import React, { useState, useEffect } from 'react';
import '../css/AdminDashboard.css';

export default function AdminDashboard() {
  const [bookings, setBookings] = useState([]);

  // Fetch all bookings (admin's view of all bookings)
  useEffect(() => {
    fetch('http://localhost:5000/booking/all-bookings', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((response) => response.json())
      .then((data) => setBookings(data))
      .catch((error) => {
        console.error('Error fetching bookings:', error);
      });
  }, []);

  const updateBookingStatus = (bookingId, status) => {
    fetch(`http://localhost:5000/booking/update-status/${bookingId}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ status }),
    })
      .then((response) => response.json())
      .then((data) => {
        setBookings(bookings.map((booking) =>
          booking.bookingId === bookingId ? { ...booking, status: data.status } : booking
        ));
      })
      .catch((error) => {
        console.error('Error updating status:', error);
      });
  };

  return (
    <div className="admin-dashboard-container">
      <h2>Admin Dashboard</h2>
      <div className="bookings-list">
        {bookings.map((booking) => (
          <div key={booking.bookingId} className="booking-card">
            <h4>{booking.service}</h4>
            <p>{`Date: ${booking.date} | Time: ${booking.timeSlot}`}</p>
            <div className={`status ${booking.status.toLowerCase()}`}>
              {booking.status}
            </div>
            <div className="status-buttons">
              {booking.status === 'Pending' && (
                <button onClick={() => updateBookingStatus(booking.bookingId, 'Started')}>Start</button>
              )}
              {booking.status === 'Started' && (
                <button onClick={() => updateBookingStatus(booking.bookingId, 'Ready')}>Ready</button>
              )}
              {booking.status === 'Ready' && (
                <button onClick={() => updateBookingStatus(booking.bookingId, 'Completed')}>Complete</button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
