import React, { useState } from "react";
import "../css/Booking.css";

const services = [
  { name: "Exterior Car Wash", price: 25, subservices: ["Basic Wash", "Premium Wash"] },
  { name: "Interior Detailing", price: 40, subservices: ["Vacuuming", "Deep Cleaning"] },
  { name: "Tire Shine", price: 15, subservices: ["Standard Shine", "Ultra Shine"] },
  { name: "Exhaust Tip Cleaning", price: 20, subservices: ["Basic Cleaning", "Advanced Polishing"] },
  { name: "Wiper Fluid Top-Up", price: 10, subservices: ["Standard Fluid", "Premium Fluid"] },
];

const timeSlots = ["9:00 AM", "11:00 AM", "1:00 PM", "3:00 PM", "5:00 PM"];

export default function Booking() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedService, setSelectedService] = useState(null);
  const [cart, setCart] = useState([]);
  const [selectedTime, setSelectedTime] = useState(null);
  const [customerName, setCustomerName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [bookingId, setBookingId] = useState(null);
  const [confirmationMessage, setConfirmationMessage] = useState(null);

  const addToCart = (service, subservice) => {
    setCart([...cart, { service, subservice, price: service.price }]);
  };

  const removeFromCart = (index) => {
    setCart(cart.filter((_, i) => i !== index));
  };

  const totalPrice = cart.reduce((sum, item) => sum + item.price, 0);

  const handleBooking = () => {
    const bookingData = {
      customerName,
      email,
      phone,
      service: selectedService.name,
      subservice: selectedService.subservices[0], // Assuming the first subservice is selected
      price: selectedService.price,
      date: selectedDate,
      timeSlot: selectedTime,
    };

    fetch("http://localhost:5000/booking/create", { // Correct URL for booking
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(bookingData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.bookingId) {
          setBookingId(data.bookingId);
          setConfirmationMessage({
            date: selectedDate,
            time: selectedTime,
            total: totalPrice,
            bookingId: data.bookingId,
          });

          setSelectedDate(null);
          setSelectedService(null);
          setSelectedTime(null);
          setCart([]);

          // Optionally, display a message
          alert(`Booking Confirmed! Booking ID: ${data.bookingId}`);
        } else {
          alert("Error: Booking failed. Please try again.");
        }
      })
      .catch((error) => {
        console.error("Error booking:", error);
        alert("There was an error with the booking process. Please try again.");
      });
  };

  const handleReviewSubmit = () => {
    const reviewData = {
      bookingId,
      rating: 5, // You can replace with actual rating from state
      reviewText: "Excellent service!", // You can replace with actual review
    };

    fetch("http://localhost:5000/review", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(reviewData),
    })
      .then((response) => response.json())
      .then((data) => {
        alert("Review submitted successfully!");
      })
      .catch((error) => {
        console.error("Error submitting review:", error);
      });
  };

  return (
    <div className="booking-container">
      <h2 className="heading">Book a Car Wash</h2>

      {/* Select Date */}
      <div className="date-picker-container">
        <h3 className="section-title">Select Date</h3>
        <input
          type="date"
          value={selectedDate || ""}
          onChange={(e) => setSelectedDate(e.target.value)}
          min={new Date().toISOString().split("T")[0]}
          className="date-picker"
        />
      </div>

      {/* Select Service */}
      {selectedDate && (
        <div className="service-container">
          <h3 className="section-title">Select Service</h3>
          <div className="service-grid">
            {services.map((service) => (
              <div
                key={service.name}
                onClick={() => setSelectedService(service)}
                className="service-item"
              >
                <h4 className="service-name">{service.name}</h4>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Select Subservice */}
      {selectedService && (
        <div className="subservice-container">
          <h3 className="section-title">Select Subservice</h3>
          <div className="subservice-grid">
            {selectedService.subservices.map((sub) => (
              <button
                key={sub}
                onClick={() => addToCart(selectedService, sub)}
                className="subservice-button"
              >
                {sub} (+${selectedService.price})
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Cart */}
      {cart.length > 0 && (
        <div className="cart-container">
          <h3 className="section-title">Cart</h3>
          <div className="cart-content">
            {cart.map((item, index) => (
              <div key={index} className="cart-item">
                <span className="cart-item-info">
                  {item.service.name} - {item.subservice}
                </span>
                <span className="cart-item-price">${item.price}</span>
                <button onClick={() => removeFromCart(index)} className="remove-button">
                  Remove
                </button>
              </div>
            ))}
            <div className="total-price">Total: ${totalPrice}</div>
          </div>
        </div>
      )}

      {/* Select Time */}
      {cart.length > 0 && (
        <div className="time-container">
          <h3 className="section-title">Select Time</h3>
          <div className="time-slot-grid">
            {timeSlots.map((time) => (
              <button
                key={time}
                onClick={() => setSelectedTime(time)}
                className="time-slot-button"
              >
                {time}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Booking Confirmation */}
      {selectedTime && (
        <div className="confirmation-container">
          <h3 className="confirmation-title">Booking Confirmed!</h3>
          <p className="confirmation-text">Date: {selectedDate}</p>
          <p className="confirmation-text">Time: {selectedTime}</p>
          <p className="confirmation-text">Total Amount: ${totalPrice}</p>
          <button className="confirm-button" onClick={handleBooking}>
            Confirm Booking
          </button>
        </div>
      )}

      {/* Confirmation Message */}
      {confirmationMessage && (
        <div className="confirmation-message">
          <h3>Booking Confirmed!</h3>
          <p>Booking ID: {confirmationMessage.bookingId}</p>
          <p>Date: {confirmationMessage.date}</p>
          <p>Time: {confirmationMessage.time}</p>
          <p>Total: ${confirmationMessage.total}</p>
        </div>
      )}

      {/* Reviews Section */}
      {bookingId && (
        <div className="reviews-section">
          <h3 className="section-title">Leave a Review</h3>
          <input
            type="number"
            max="5"
            min="1"
            value={rating}
            onChange={(e) => setRating(Number(e.target.value))}
            placeholder="Rating (1 to 5)"
          />
          <textarea
            value={reviewText}
            onChange={(e) => setReviewText(e.target.value)}
            placeholder="Write your review here..."
          />
          <button onClick={handleReviewSubmit}>Submit Review</button>
        </div>
      )}
    </div>
  );
}
