import React, { useState, useEffect } from 'react';
import api from '../config/axios';
import '../css/dashboard.css';

const AdminDashboard = () => {
  // Authentication states
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [accessCode, setAccessCode] = useState('');
  const [authError, setAuthError] = useState('');
  const [authLoading, setAuthLoading] = useState(false)
  
  // Dashboard data states
  const [dashboardData, setDashboardData] = useState(null);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  // User detail states
  const [selectedUser, setSelectedUser] = useState(null);
  const [userDetails, setUserDetails] = useState(null);
  const [detailsLoading, setDetailsLoading] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    full_name: '',
    email: '',
    phone_number: '',
    address: ''
  });
  const [updateSuccess, setUpdateSuccess] = useState('');
  
  // Check if admin is already authenticated
  useEffect(() => {
    const token = localStorage.getItem('adminToken');
    if (token) {
      setIsAuthenticated(true);
      fetchDashboardData();
      fetchUsers();
    }
  }, []);
  
  // Handle admin code verification
  const handleVerifyCode = async (e) => {
    e.preventDefault();
    setAuthError('');
    setAuthLoading(true);
    
    try {
      // Use the API instance with the correct base URL
      const response = await api.post('/api/admin/verify-code', { accessCode });
      localStorage.setItem('adminToken', response.data.token);
      setIsAuthenticated(true);
      setAuthLoading(false);
      fetchDashboardData();
      fetchUsers();
    } catch (err) {
      console.error('Auth error:', err);
      setAuthError(err.response?.data?.message || 'Verification failed');
      setAuthLoading(false);
    }
  };
  
  // Fetch dashboard summary data
  const fetchDashboardData = async () => {
    setLoading(true);
    setError('');
    
    try {
      const token = localStorage.getItem('adminToken');
      const response = await api.get('/api/admin/summary', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setDashboardData(response.data);
      setLoading(false);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load dashboard data');
      setLoading(false);
      
      // If token is invalid, log out
      if (err.response?.status === 401) {
        handleLogout();
      }
    }
  };
  
  // Fetch all users
  const fetchUsers = async () => {
    setLoading(true);
    setError('');
    
    try {
      const token = localStorage.getItem('adminToken');
      const response = await api.get('/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setUsers(response.data);
      setLoading(false);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load users');
      setLoading(false);
      
      // If token is invalid, log out
      if (err.response?.status === 401) {
        handleLogout();
      }
    }
  };
  
  // Fetch user details
  const fetchUserDetails = async (userId) => {
    setDetailsLoading(true);
    setError('');
    
    try {
      const token = localStorage.getItem('adminToken');
      const response = await api.get(`/api/admin/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setUserDetails(response.data);
      setFormData({
        username: response.data.user.username,
        full_name: response.data.user.full_name || '',
        email: response.data.user.email,
        phone_number: response.data.user.phone_number || '',
        address: response.data.user.address || ''
      });
      setDetailsLoading(false);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load user details');
      setDetailsLoading(false);
    }
  };
  
  // Handle user selection
  const handleUserSelect = (userId) => {
    setSelectedUser(userId);
    setEditMode(false);
    setUpdateSuccess('');
    fetchUserDetails(userId);
  };
  
  // Handle form input changes
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };
  
  // Handle user update
  const handleUpdateUser = async (e) => {
    e.preventDefault();
    setError('');
    
    try {
      const token = localStorage.getItem('adminToken');
      const response = await api.put(`/api/admin/users/${selectedUser}`, formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      // Update the user in the list
      setUsers(users.map(user => 
        user.id === selectedUser ? { ...user, ...formData } : user
      ));
      
      // Update user details
      setUserDetails({
        ...userDetails,
        user: {
          ...userDetails.user,
          ...formData
        }
      });
      
      setUpdateSuccess('User updated successfully');
      setEditMode(false);
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setUpdateSuccess('');
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update user');
    }
  };
  
  // Handle booking status update
  const handleUpdateBooking = async (bookingId, status) => {
    try {
      const token = localStorage.getItem('adminToken');
      await api.put(`/api/admin/bookings/${bookingId}`, 
        { status, notes: `Status changed to ${status} by admin` },
        { headers: { Authorization: `Bearer ${token}` }}
      );
      
      // Update booking in user details
      setUserDetails({
        ...userDetails,
        bookings: userDetails.bookings.map(booking => 
          booking.id === bookingId ? { ...booking, status } : booking
        )
      });
      
      setUpdateSuccess(`Booking #${bookingId} updated to ${status}`);
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setUpdateSuccess('');
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update booking');
    }
  };
  
  // Handle admin logout
  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    setIsAuthenticated(false);
    setSelectedUser(null);
    setUserDetails(null);
    setDashboardData(null);
    setUsers([]);
  };
  
  // Render access code verification screen
  if (!isAuthenticated) {
    return (
      <div className="admin-auth-container">
        <div className="admin-auth-card">
          <h1>Car Wash Admin</h1>
          <p>Enter your 6-digit access code to continue</p>
          
          {authError && <div className="admin-auth-error">{authError}</div>}
          
          <form onSubmit={handleVerifyCode}>
            <input
              type="text"
              placeholder="6-digit access code"
              value={accessCode}
              onChange={(e) => setAccessCode(e.target.value)}
              maxLength={6}
              required
            />
            
            <button 
              type="submit" 
              disabled={accessCode.length !== 6 || authLoading}
            >
              {authLoading ? 'Verifying...' : 'Access Dashboard'}
            </button>
          </form>
        </div>
      </div>
    );
  }
  
  // Rest of the component remains the same
  
  return (
    <div className="admin-dashboard-container">
      {/* Admin Header */}
      <div className="admin-header">
        <div className="admin-header-left">
          <h1>Car Wash Admin Dashboard</h1>
        </div>
        <div className="admin-header-right">
          <button className="admin-logout-btn" onClick={handleLogout}>
            Logout <i className="fas fa-sign-out-alt"></i>
          </button>
        </div>
      </div>
      
      {/* Error and success messages */}
      {error && <div className="admin-error">{error}</div>}
      {updateSuccess && <div className="admin-success">{updateSuccess}</div>}
      
      {/* Dashboard content - same as original */}
      <div className="admin-dashboard-content">
        {/* Left panel - User list */}
        <div className="admin-left-panel">
          {/* ... rest of component (unchanged) ... */}
          {/* You can keep the original code for the rest of the component */}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
