import React, { useState } from 'react';
import axios from 'axios';
import GoogleLogin from './GoogleLogin';
import ForgotPassword from './ForgotPassword'; 


const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isForgotPassword, setIsForgotPassword] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = { username, password };
    try {
      const response = await axios.post('http://localhost:5000/login', data);
      localStorage.setItem('token', response.data.token);
      alert('Login successful');
    } catch (error) {
      console.error('Error logging in', error);
      alert('Login failed');
    }
  };

  // Handle Forgot Password Submit
  const handleForgotPasswordSubmit = async (email) => {
    setIsSubmitting(true);
    try {
      const response = await axios.post('http://localhost:5000/forgot-password', { email });
      alert(response.data.message);
      setIsForgotPassword(false); // Hide forgot password form after submission
    } catch (error) {
      console.error('Error sending reset email', error);
      alert('Error: ' + error.response?.data?.message || 'Unknown error');
    }
    setIsSubmitting(false);
  };

  return (
    <div className="auth-form">
      <h2>Login</h2>
      {!isForgotPassword ? (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" disabled={isSubmitting}>
            {isSubmitting ? 'Logging in...' : 'Login'}
          </button>
          <p>
            <a href="#" onClick={() => setIsForgotPassword(true)}>Forgot Password?</a>
          </p>
        </form>
      ) : (
        <ForgotPassword onSubmit={handleForgotPasswordSubmit} isSubmitting={isSubmitting} />
      )}
      <GoogleLogin />
    </div>
  );
};

export default Login;
