import React, { useState, useEffect, useContext } from 'react';

import { UserContext } from '../context/UserContext'; // Import UserContext
import '../css/MyBookings.css'; // Custom CSS for mobile responsiveness

export default function MyBookings() {
  const { user } = useContext(UserContext); // Access user from context
  const [bookings, setBookings] = useState([]);

  // Fetch user bookings if the user is logged in
  useEffect(() => {
    if (user && user.userId) {
      // Now use user.userId to fetch bookings
      fetch(`http://localhost:5000/booking/user-bookings/${user.userId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      })
        .then((response) => response.json())
        .then((data) => setBookings(data))
        .catch((error) => {
          console.error('Error fetching bookings:', error);
        });
    } else {
      console.log('No user logged in');
    }
  }, [user]); // Re-run if the user context changes

  return (
    <div className="my-bookings-container">
      <h2>My Bookings</h2>
      {bookings.length > 0 ? (
        bookings.map((booking) => (
          <div className="booking-card" key={booking.bookingId}>
            <div className="booking-info">
              <h4>{booking.service}</h4>
              <p>{`Date: ${booking.date} | Time: ${booking.timeSlot}`}</p>
              <p>{`Total: $${booking.total}`}</p>
              <div className={`status ${booking.status.toLowerCase()}`}>
                {booking.status}
              </div>
            </div>
          </div>
        ))
      ) : (
        <p>No bookings yet.</p>
      )}
    </div>
  );
}
