import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import App1 from "./components/app1"; 
import Login from "./components/Login";
import Register from "./components/Register";
import CompleteProfile from "./components/CompleteProfile";
import ResetPassword from "./components/ResetPassword";
import HomePage from "./components/HomePage";
import Booking from './components/Booking'; 
import MyBooking from './components/MyBooking';
import "./css/styles.css";

// Firebase
import { initializeApp } from "firebase/app";
import { getAuth, GoogleAuthProvider, signInWithPopup, onAuthStateChanged, signOut } from "firebase/auth";

// Firebase config
const firebaseConfig = {
  apiKey: "AIzaSyDszpBw65FzuIrITEW53ydo0KM0jSIzNqg",
  authDomain: "carwash-b2a26.firebaseapp.com",
  projectId: "carwash-b2a26",
  storageBucket: "carwash-b2a26.appspot.com",
  messagingSenderId: "1993606131",
  appId: "1:1993606131:web:93ad9ed1e791e81d674886",
  measurementId: "G-4339HCF1BJ",
};

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);

const GoogleLoginButton = () => {
  const [user, setUser] = React.useState(null);

  // Listen for authentication state changes
  React.useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser ?? null);
    });
    return unsubscribe;
  }, []);

  const handleLogin = async () => {
    const provider = new GoogleAuthProvider();
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (error) {
      console.error("Error during Google sign-in: ", error);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
    } catch (error) {
      console.error("Error during sign-out: ", error);
    }
  };

  return (
    <div>
      {!user ? (
        <button onClick={handleLogin} className="google-login-btn">
          Sign in with Google
        </button>
      ) : (
        <div>
          <h3>Welcome, {user.displayName}</h3>
          <p>Email: {user.email}</p>
          <button onClick={handleLogout} className="google-logout-btn">
            Sign Out
          </button>
        </div>
      )}
    </div>
  );
};

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<App1 />} />
        <Route path="/homepage" element={<HomePage />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/bookings" element={<Booking />} />
        <Route path="/Mybooking" component={MyBooking} />
        <Route path="/google-login" element={<GoogleLoginButton />} />
        <Route path="/complete-profile" element={<CompleteProfile />} />
        <Route path="/reset-password/:token" element={<ResetPassword />} />
      </Routes>
    </Router>
  );
}

export default App;
