import React, { useState, useEffect, useContext } from 'react';
import UserContext from '../context/UserContext';  // Import UserContext
import '../css/MyBookings.css'; // Custom CSS for styling

export default function MyBookings() {
  const { userData } = useContext(UserContext);  // Get the user data from context
  const [bookings, setBookings] = useState([]);   // State to hold the bookings

  // Fetch user bookings if the user is logged in
  useEffect(() => {
    if (userData && userData.userId) {
      // Fetch bookings from the backend using the userId from userData
      fetch(`http://localhost:5000/booking/user-bookings/${userData.userId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      })
        .then((response) => response.json())
        .then((data) => {
          if (data && Array.isArray(data)) {
            setBookings(data);  // Set the fetched bookings in the state
          } else {
            console.log('No bookings found for this user.');
          }
        })
        .catch((error) => {
          console.error('Error fetching bookings:', error);  // Handle errors
        });
    } else {
      console.log('No user logged in');  // No user data, handle this case
    }
  }, [userData]);  // Re-run effect if userData changes

  return (
    <div className="my-bookings-container">
      <h2>My Bookings</h2>
      {bookings.length > 0 ? (
        bookings.map((booking) => (
          <div className="booking-card" key={booking.bookingId}>
            <div className="booking-info">
              <h4>{booking.service}</h4>
              <p>{`Date: ${booking.date} | Time: ${booking.timeSlot}`}</p>
              <p>{`Total: $${booking.total}`}</p>
              <div className={`status ${booking.status.toLowerCase()}`}>
                {booking.status}
              </div>
            </div>
          </div>
        ))
      ) : (
        <p>No bookings yet.</p>
      )}
    </div>
  );
}
