import React, { useState, useEffect, useContext } from 'react';
import UserContext from '../context/UserContext';
import axios from 'axios';
import '../css/MyBookings.css';

export default function MyBookings() {
  const { userData, setUser } = useContext(UserContext);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [cancelSuccess, setCancelSuccess] = useState('');

  // Use the bookings data directly from userData stored in context
  useEffect(() => {
    if (userData && userData.bookings && Array.isArray(userData.bookings)) {
      setBookings(userData.bookings);
    } else {
      console.log('No bookings found in user data.');
    }
  }, [userData]);

  // Function to cancel a booking
  const handleCancelBooking = async (bookingId) => {
    if (!window.confirm('Are you sure you want to cancel this booking?')) {
      return;
    }

    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `/api/bookings/${bookingId}/cancel`,
        { status: 'cancelled' },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Update the bookings list
      const updatedBookings = bookings.map(booking => 
        booking.id === bookingId ? { ...booking, status: 'cancelled' } : booking
      );
      
      // Update local state
      setBookings(updatedBookings);
      
      // If userData has bookings, update them there too
      if (userData && userData.bookings) {
        const updatedUserData = {
          ...userData,
          bookings: updatedBookings
        };
        setUser(updatedUserData);
      }
      
      setCancelSuccess(`Booking #${bookingId} has been cancelled successfully.`);
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setCancelSuccess('');
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to cancel booking. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Helper function to determine if a booking can be cancelled by the user
  const canCancelBooking = (status) => {
    // Users can only cancel bookings that are in pending, confirmed, or work started status
    const cancellableStatuses = ['pending', 'confirmed', 'work started'];
    return cancellableStatuses.includes(status?.toLowerCase() || 'pending');
  };

  return (
    <div className="my-bookings-container">
      <h2>My Bookings</h2>
      
      {/* Success and error messages */}
      {cancelSuccess && <div className="booking-success">{cancelSuccess}</div>}
      {error && <div className="booking-error">{error}</div>}
      {loading && <div className="booking-loading">Processing your request...</div>}
      
      {bookings.length > 0 ? (
        bookings.map((booking) => {
          // Safely handle 'status' and ensure it's a string
          const status = booking.status ? booking.status.toLowerCase() : 'pending';
          
          return (
            <div className="booking-card" key={booking.id}>
              <div className="booking-header">
                <h3>Booking ID: {booking.id}</h3>
                <span className={`status ${status}`}>{booking.status || 'Pending'}</span>
              </div>
              <div className="booking-details">
                <div className="booking-info">
                  <p><strong>Customer Name:</strong> {booking.customer_name}</p>
                  <p><strong>Email:</strong> {booking.email}</p>
                  <p><strong>Phone:</strong> {booking.phone}</p>
                  <p><strong>Service:</strong> {booking.service}</p>
                  <p><strong>Subservice:</strong> {booking.subservice}</p>
                </div>
                <div className="booking-car-details">
                  <p><strong>Car Plate:</strong> {booking.plate_number}</p>
                  <p><strong>Model:</strong> {booking.model}</p>
                  <p><strong>Color:</strong> {booking.color}</p>
                  <p><strong>Seats:</strong> {booking.seats}</p>
                </div>
                <div className="booking-price">
                  <p><strong>Price:</strong> ${booking.price}</p>
                </div>
                <div className="booking-time">
                  <p><strong>Date:</strong> {booking.date}</p>
                  <p><strong>Time:</strong> {booking.time_slot}</p>
                </div>
                {/* Status explanation */}
                <div className="booking-status-info">
                  {status === 'pending' && (
                    <p>Your booking is awaiting confirmation.</p>
                  )}
                  {status === 'confirmed' && (
                    <p>Your booking has been confirmed. Please bring your car at the scheduled time.</p>
                  )}
                  {status === 'work started' && (
                    <p>We're currently servicing your car.</p>
                  )}
                  {status === 'ready to collect' && (
                    <p>Your car is ready! You can pick it up at your convenience.</p>
                  )}
                  {status === 'delivered' && (
                    <p>Service completed and car delivered. Thank you for your business!</p>
                  )}
                  {status === 'cancelled' && (
                    <p>This booking has been cancelled.</p>
                  )}
                </div>
                {/* Cancel button */}
                {canCancelBooking(status) && (
                  <div className="booking-actions">
                    <button 
                      className="cancel-booking-btn"
                      onClick={() => handleCancelBooking(booking.id)}
                      disabled={loading}
                    >
                      Cancel Booking
                    </button>
                  </div>
                )}
              </div>
            </div>
          );
        })
      ) : (
        <p>No bookings yet.</p>
      )}
    </div>
  );
}
