import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import UserContext from '../context/UserContext';
import '../css/MyBookings.css'; // Make sure you have this CSS file

const MyBookings = () => {
  const { userData, refreshUserData } = useContext(UserContext);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [statusMessage, setStatusMessage] = useState(null);

  // Refresh data when component loads
  useEffect(() => {
    if (userData) {
      refreshUserData()
        .then(() => setLoading(false))
        .catch(err => {
          console.error("Error refreshing bookings data:", err);
          setError("Failed to load your bookings. Please try again.");
          setLoading(false);
        });
    } else {
      setLoading(false);
    }
  }, [refreshUserData, userData]);

  const cancelBooking = async (bookingId) => {
    if (!window.confirm("Are you sure you want to cancel this booking?")) {
      return;
    }
    
    try {
      setStatusMessage({ type: 'info', text: 'Cancelling booking...' });
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/booking/${bookingId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setStatusMessage({ type: 'success', text: 'Booking cancelled successfully' });
      
      // Refresh user data to update bookings list
      await refreshUserData();
    } catch (error) {
      console.error('Error cancelling booking:', error);
      setStatusMessage({ 
        type: 'error', 
        text: error.response?.data?.message || 'Failed to cancel booking'
      });
    }
  };

  // Helper function to get status class for styling
  const getStatusClass = (status) => {
    switch (status) {
      case 'pending': return 'status-pending';
      case 'confirmed': return 'status-confirmed';
      case 'in_progress': return 'status-in-progress';
      case 'completed': return 'status-completed';
      case 'cancelled': return 'status-cancelled';
      default: return '';
    }
  };

  // Function to format date for display
  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US', { 
      weekday: 'short', 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric' 
    });
  };

  if (loading) {
    return <div className="loading">Loading your bookings...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!userData) {
    return <div className="error-message">Please log in to view your bookings</div>;
  }

  // Get bookings from userData
  const bookings = userData.bookings || [];

  // Only show active bookings by default (not cancelled or completed)
  const activeBookings = bookings.filter(booking => 
    booking.status !== 'cancelled' && booking.status !== 'completed'
  );

  // Show past bookings (cancelled or completed)
  const pastBookings = bookings.filter(booking => 
    booking.status === 'cancelled' || booking.status === 'completed'
  );

  // Function to get car details for a booking
  const getCarDetails = (carId) => {
    const car = userData.cars?.find(car => car.id === carId);
    if (!car) return 'Unknown vehicle';
    return `${car.model} (${car.plate_number})`;
  };

  return (
    <div className="my-bookings-container">
      <div className="bookings-header">
        <h2>My Bookings</h2>
        <Link to="/booking" className="new-booking-button">New Booking</Link>
      </div>

      {statusMessage && (
        <div className={`status-message ${statusMessage.type}`}>
          {statusMessage.text}
        </div>
      )}

      {bookings.length === 0 ? (
        <div className="no-bookings">
          <p>You don't have any bookings yet.</p>
          <Link to="/booking" className="booking-link">Make your first booking</Link>
        </div>
      ) : (
        <>
          <h3>Upcoming & Active Bookings</h3>
          {activeBookings.length === 0 ? (
            <p>No upcoming bookings</p>
          ) : (
            <div className="bookings-list">
              {activeBookings.map(booking => (
                <div className="booking-card" key={booking.id}>
                  <div className="booking-header">
                    <h4>Service: {booking.service_type}</h4>
                    <span className={`booking-status ${getStatusClass(booking.status)}`}>
                      {booking.status.replace('_', ' ')}
                    </span>
                  </div>
                  <div className="booking-details">
                    <p><strong>Date:</strong> {formatDate(booking.booking_date)}</p>
                    <p><strong>Time:</strong> {booking.booking_time}</p>
                    <p><strong>Vehicle:</strong> {getCarDetails(booking.car_id)}</p>
                    {booking.special_requests && (
                      <p><strong>Special Requests:</strong> {booking.special_requests}</p>
                    )}
                  </div>
                  <div className="booking-actions">
                    {booking.status === 'pending' && (
                      <button 
                        className="cancel-button"
                        onClick={() => cancelBooking(booking.id)}
                      >
                        Cancel
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}

          <h3>Past Bookings</h3>
          {pastBookings.length === 0 ? (
            <p>No past bookings</p>
          ) : (
            <div className="bookings-list past-bookings">
              {pastBookings.map(booking => (
                <div className="booking-card" key={booking.id}>
                  <div className="booking-header">
                    <h4>Service: {booking.service_type}</h4>
                    <span className={`booking-status ${getStatusClass(booking.status)}`}>
                      {booking.status.replace('_', ' ')}
                    </span>
                  </div>
                  <div className="booking-details">
                    <p><strong>Date:</strong> {formatDate(booking.booking_date)}</p>
                    <p><strong>Time:</strong> {booking.booking_time}</p>
                    <p><strong>Vehicle:</strong> {getCarDetails(booking.car_id)}</p>
                    {booking.special_requests && (
                      <p><strong>Special Requests:</strong> {booking.special_requests}</p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default MyBookings;
