import React, { useState } from 'react';
import axios from 'axios';
import GoogleLogin from './GoogleLogin';
import ForgotPassword from './ForgotPassword';
import "../css/auth.css";
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isForgotPassword, setIsForgotPassword] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate(); // Create navigate function to redirect

  // Handle Login Submit
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Normalize email/username (lowercase) before sending request to server
    const data = { username: username.toLowerCase(), password }; // Ensure email or username is lowercase

    try {
      const response = await axios.post('http://localhost:5000/login', data);
      localStorage.setItem('token', response.data.token); // Store token in localStorage
      alert('Login successful');
      navigate('/'); // Redirect to homepage after login
    } catch (error) {
      console.error('Error logging in', error);
      alert('Login failed');
    }
  };

  // Handle Forgot Password Submit
  const handleForgotPasswordSubmit = async (email) => {
    setIsSubmitting(true);
    try {
      const response = await axios.post('http://localhost:5000/forgot-password', { email });
      alert(response.data.message);
      setIsForgotPassword(false); // Hide forgot password form after submission
    } catch (error) {
      console.error('Error sending reset email', error);
      alert('Error: ' + error.response?.data?.message || 'Unknown error');
    }
    setIsSubmitting(false);
  };

  return (
    <div className="auth-form-container">
      <div className="auth-form">
        <h2>Login</h2>
        {!isForgotPassword ? (
          <form onSubmit={handleSubmit} className="login-form">
            <input
              type="text"
              placeholder="Fullname or Email"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              className="auth-input"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="auth-input"
            />
            <button type="submit" className="auth-btn" disabled={isSubmitting}>
              {isSubmitting ? 'Logging in...' : 'Login'}
            </button>
            <p className="forgot-password-link">
              <a href="#" onClick={() => setIsForgotPassword(true)}>Forgot Password?</a>
            </p>
            <p className="register-link">
              Donâ€™t have an account? <a href="/register">Register!</a>
            </p>
          </form>
        ) : (
          <ForgotPassword onSubmit={handleForgotPasswordSubmit} isSubmitting={isSubmitting} />
        )}
        <GoogleLogin />
      </div>
    </div>
  );
};

export default Login;
