import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import UserContext from '../context/UserContext'; // Import UserContext

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { setUser } = useContext(UserContext); // Access setUser from context
  const navigate = useNavigate();

  // Handle Login Submit
  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = { username: username.toLowerCase(), password }; // Normalize username to lowercase

    try {
      const response = await axios.post('http://localhost:5000/login', data, { withCredentials: true });

      const { token } = response.data;
      localStorage.setItem('token', token); // Save token to localStorage

      // Now fetch the user data and store it in context
      const userResponse = await axios.get('http://localhost:5000/api/profile', {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Store the user data in context and localStorage
      setUser(userResponse.data);

      navigate('/'); // Redirect to homepage or dashboard
    } catch (error) {
      console.error('Error logging in', error);
      setErrorMessage('Login failed. Please check your credentials and try again.');
    }
  };

  return (
    <div className="auth-form-container">
      <div className="auth-form">
        <h2>Login</h2>

        {errorMessage && <div className="error-message">{errorMessage}</div>}

        <form onSubmit={handleSubmit} className="login-form">
          <input
            type="text"
            placeholder="Username or Email"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            className="auth-input"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="auth-input"
          />
          <button type="submit" className="auth-btn" disabled={isSubmitting}>
            {isSubmitting ? 'Logging in...' : 'Login'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
